CREATE  TABLE IF NOT EXISTS `mdtb_groupes` (   `group_ID` INT(11) NOT NULL AUTO_INCREMENT ,   `group_Nom` TEXT NOT NULL ,   PRIMARY KEY (`group_ID`) ) ENGINE = INNODB DEFAULT CHARACTER SET = latin1;  
CREATE  TABLE IF NOT EXISTS `mdtb_users` (   `user_ID` INT(11) NOT NULL AUTO_INCREMENT ,   `group_ID` INT(11) NOT NULL ,   `user_Login` VARCHAR(20) NOT NULL ,   `user_Name` TEXT NOT NULL ,   `user_Mail` TEXT NOT NULL ,   `user_Password` VARCHAR(40) NOT NULL ,   `user_Rank` VARCHAR(5) NOT NULL ,   PRIMARY KEY (`user_ID`) ,   UNIQUE INDEX `user_Login` (`user_Login` ASC) ,   INDEX `group_ID` (`group_ID` ASC) ,   INDEX `fk_mdtb_users_mdtb_groupes` (`group_ID` ASC) ,   CONSTRAINT `fk_mdtb_users_mdtb_groupes`     FOREIGN KEY (`group_ID` )     REFERENCES  `mdtb_groupes` (`group_ID` )     ON DELETE NO ACTION     ON UPDATE NO ACTION) ENGINE = INNODB DEFAULT CHARACTER SET = latin1;
CREATE  TABLE IF NOT EXISTS  `mdtb_users_rights` (   `usri_ID` INT(11) NOT NULL AUTO_INCREMENT ,   `user_ID` INT(11) NOT NULL ,   `group_ID` INT(11) NOT NULL DEFAULT '-1' ,   `usri_Rights` INT(4) NOT NULL ,   `usri_Table` VARCHAR(20) NOT NULL ,   `usri_Record_ID` INT(11) NOT NULL DEFAULT '-1' ,   `usri_SQLFilter` TEXT NOT NULL ,   PRIMARY KEY (`usri_ID`) ,   INDEX `usri_table` (`usri_Table` ASC, `usri_Record_ID` ASC) ,   INDEX `group_ID` (`group_ID` ASC) ,   INDEX `fk_mdtb_users_rights_mdtb_users1` (`user_ID` ASC) ,   INDEX `fk_mdtb_users_rights_mdtb_groupes1` (`group_ID` ASC) ,   CONSTRAINT `fk_mdtb_users_rights_mdtb_users1`     FOREIGN KEY (`user_ID` )     REFERENCES  `mdtb_users` (`user_ID` )     ON DELETE NO ACTION     ON UPDATE NO ACTION,   CONSTRAINT `fk_mdtb_users_rights_mdtb_groupes1`     FOREIGN KEY (`group_ID` )     REFERENCES  `mdtb_groupes` (`group_ID` )     ON DELETE NO ACTION     ON UPDATE NO ACTION) ENGINE = INNODB DEFAULT CHARACTER SET = latin1;
