CREATE TABLE IF NOT EXISTS `rel_secteurme_fichier` (`id_rel` INT NOT NULL AUTO_INCREMENT PRIMARY KEY ,`code_entite` VARCHAR( 8 ) NOT NULL ,`type_fichier` VARCHAR( 5 ) NOT NULL ,`nom_fichier` TEXT NOT NULL ,INDEX ( `code_entite` , `type_fichier` )) ENGINE = MYISAM ;